process {
   cpus = { check_max( 1    * task.attempt, 'cpus'   ) }
   memory = { check_max( 6.GB * task.attempt, 'memory' ) }
   time = { check_max( 4.h  * task.attempt, 'time'   ) }
   errorStrategy = 'retry'
   maxRetries = 2
   maxErrors = '-1'
   withLabel:process_single {
      cpus = { check_max( 1                  , 'cpus'    ) }
      memory = { check_max( 6.GB * task.attempt, 'memory'  ) }
      time = { check_max( 4.h  * task.attempt, 'time'    ) }
   }
   withLabel:process_low {
      cpus = { check_max( 2      * task.attempt, 'cpus'   ) }
      memory = { check_max( 12.GB  * task.attempt, 'memory' ) }
      time = { check_max( 4.h    * task.attempt, 'time'   ) }
   }
   withLabel:process_medium {
      cpus = { check_max( 6      * task.attempt, 'cpus'   ) }
      memory = { check_max( 36.GB  * task.attempt, 'memory' ) }
      time = { check_max( 8.h    * task.attempt, 'time'   ) }
   }
   withLabel:process_high {
      cpus = { check_max( 12     * task.attempt, 'cpus'   ) }
      memory = { check_max( 72.GB  * task.attempt, 'memory' ) }
      time = { check_max( 16.h   * task.attempt, 'time'   ) }
   }
   withLabel:process_long {
      time = { check_max( 20.h   * task.attempt, 'time'   ) }
   }
   withLabel:process_high_memory {
      memory = { check_max( 200.GB * task.attempt, 'memory' ) }
   }
   withLabel:error_ignore {
      errorStrategy = 'ignore'
   }
   withLabel:error_retry {
      errorStrategy = 'retry'
      maxRetries = 2
   }
   withName:CUSTOM_DUMPSOFTWAREVERSIONS {
      cache = false
      publishDir = [path:{ "${params.outdir}/pipeline_info" }, mode:'copy', pattern:'*_versions.yml']
   }
   withName:PLASMIDID {
      errorStrategy = 'ignore'
   }
   shell = ['/bin/bash', '-euo', 'pipefail']
   publishDir = [path:{ "${params.outdir}/${task.process.tokenize(':')[-1].tokenize('_')[0].toLowerCase()}" }, mode:'copy', saveAs:{ filename -> filename.equals('versions.yml') ? null : filename }]
   withName:SAMPLESHEET_CHECK {
      publishDir = [path:{ "${params.outdir}/pipeline_info" }, mode:'copy', saveAs:{ filename -> filename.equals('versions.yml') ? null : filename }]
   }
   withName:'.*:.*:PREPARE_GENOME:GUNZIP_.*' {
      publishDir = [path:ScriptDF8044060054E44F9089B2B3ABA330E8$_run_closure2$_closure45$_closure49@68e62b3b, mode:'copy', saveAs:ScriptDF8044060054E44F9089B2B3ABA330E8$_run_closure2$_closure45$_closure50@34c2e03d, enabled:false]
   }
   withName:'.*:.*:PREPARE_GENOME:UNTAR_.*' {
      ext {
